#!/usr/bin/env ruby

require_relative "codontable"


# Make inverse codon table
inverse_codon_table={}
$rna_codon_table.each_pair do |name, val| 
	inverse_codon_table[val]=[] if inverse_codon_table[val]==nil
	inverse_codon_table[val]<<name
end

protein_seq=""


num_rnas = 3
protein_seq.chars.each do |aa|  
	num_rnas*=inverse_codon_table[aa].length
	num_rnas = num_rnas % 1000000
end


puts num_rnas